import { Component, OnInit } from '@angular/core';
import { HeroesService, Heroe } from '../../services/heroes.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-heroes',
  templateUrl: './heroes.component.html'
})
export class HeroesComponent implements OnInit{

  heroes:Heroe[] = [];

  constructor( private _heroesService:HeroesService,
    private router:Router) {

  }

  verHeroe( idx:number){
    this.router.navigate(['/heroe', idx]);
  }

  ngOnInit(){
    this.heroes = this._heroesService.getHeroes();
    console.log(this.heroes)
  }

}
<h1>Héroes - Marvel y DC</h1>
<hr>


<div class="row">
    <div class="row">                                            

        <!-- *ngFor="let heroe of heroes; let i = index" -->
        <div class="col-md-4 border" >
            <img class="card-img-top img-fluid" [src]="heroe.img" [alt]="heroe.nombre">
            <div class="card-block">
                <h5 class="card-title">{{ heroe.nombre }}</h5>
                <p class="card-text"> {{ heroe.bio }}</p>
                <p class="card-text"><small class="text-muted"> {{ heroe.aparicion }}</small></p>

<!--                 <button type="button" class="btn btn-outline-primary btn-block">Ver más...</button> -->
                <a [routerLink]="['/heroe', i]" class="btn btn-outline-primary">Ver más link...</a>

              </div>
        </div>

    </div>
</div>
