import { Component } from '@angular/core';
import { ActivatedRoute, Route } from '@angular/router';
import { HeroesService } from 'src/app/services/heroes.service';



@Component({
  selector: 'app-heroe',
  templateUrl: './heroe.component.html',
  styleUrls: ['./heroe.component.css']
})
export class HeroeComponent {

  heroe:any = {};

  constructor ( private activatedRoute: ActivatedRoute,
                private _heroesService: HeroesService){

    this.activatedRoute.params.subscribe( params =>{
      console.log( params['id'] );
      this.heroe = this._heroesService.getHeroe(params['id']);
      console.log(this.heroe);

    })
  }
}
<h1>Nombre del heroe: / Fecha de aparición: </h1>
<hr>

<div class="row">

    <div class="col-md-4">
<!--    <img *ngIf="{{ heroe.casa }} === 'x' " src="" class="img-fluid" alt="Imagen X">
        <img *ngIf="{{ heroe.casa }} === 'y'" src="ruta-a-imagen-y.jpg" class="img-fluid" alt="Imagen Y"> -->

        <br><br>
        <a href="heroes" class="btn btn-outline-danger btn-block">Regresar</a>
    </div>

    <div class="col-md-8">
        <h3>{{ heroe.nombre }} - {{ heroe.aparicion }}</h3>
        <hr>
        <p>
            {{ heroe.bio }}
        </p>
        <div class="">
            <img src=" {{ heroe.img }}" alt="">
        </div>


    </div>

</div>
