        <br><br>
        <a href="heroes" class="btn btn-outline-danger btn-block">Regresar</a>
    </div>

    <div class="col-md-8">
        <h3>{{ heroe.nombre }} - </h3>
        <hr>
        <p>
            {{ heroe.bio }}
        </p>
        <div class="">
            <img src="" alt="">
        </div>


    </div>

</div>
